    margin:20px;
`
const Filter = styled.div`
     margin:20px;

`
const FilterText =styled.span`
    font-size:20px;
    font-weight:600;
    margin-right: 20px;

`
const Select = styled.select`
    padding:10px;
    margin-right:20px;
