            <NavBar/>
            <Announsment/>
            <Tittle>Shoes</Tittle>
            <FilterContainer>
                <Filter> 
                <FilterText>Filter Products:</FilterText>
                <Select>
                    <Option disabled selected>Color</Option>
                    <Option>Black</Option>
                    <Option>White</Option>
                    <Option>Brown</Option>
                    <Option>Dark</Option>
                    <Option>Red </Option>
                </Select>
                <Select>
                    <Option disabled selected>Size</Option>
                    <Option>XL</Option>
                    <Option>X</Option>
                    <Option>M</Option>
                    <Option>S</Option>
